--
-- Table structure for table `tbdb_content`
--

DROP TABLE IF EXISTS `tbdb_content`;
CREATE TABLE IF NOT EXISTS `tbdb_content` (
  `id` int(11) unsigned NOT NULL auto_increment,
  `title` varchar(255) NOT NULL default '',
  `alias` varchar(255) NOT NULL default '',
  `title_alias` varchar(255) NOT NULL default '',
  `introtext` mediumtext NOT NULL,
  `fulltext` mediumtext NOT NULL,
  `state` tinyint(3) NOT NULL default '0',
  `sectionid` int(11) unsigned NOT NULL default '0',
  `mask` int(11) unsigned NOT NULL default '0',
  `catid` int(11) unsigned NOT NULL default '0',
  `created` datetime NOT NULL default '0000-00-00 00:00:00',
  `created_by` int(11) unsigned NOT NULL default '0',
  `created_by_alias` varchar(255) NOT NULL default '',
  `modified` datetime NOT NULL default '0000-00-00 00:00:00',
  `modified_by` int(11) unsigned NOT NULL default '0',
  `checked_out` int(11) unsigned NOT NULL default '0',
  `checked_out_time` datetime NOT NULL default '0000-00-00 00:00:00',
  `publish_up` datetime NOT NULL default '0000-00-00 00:00:00',
  `publish_down` datetime NOT NULL default '0000-00-00 00:00:00',
  `images` text NOT NULL,
  `urls` text NOT NULL,
  `attribs` text NOT NULL,
  `version` int(11) unsigned NOT NULL default '1',
  `parentid` int(11) unsigned NOT NULL default '0',
  `ordering` int(11) NOT NULL default '0',
  `metakey` text NOT NULL,
  `metadesc` text NOT NULL,
  `access` int(11) unsigned NOT NULL default '0',
  `hits` int(11) unsigned NOT NULL default '0',
  `metadata` text NOT NULL,
  PRIMARY KEY  (`id`),
  KEY `idx_section` (`sectionid`),
  KEY `idx_access` (`access`),
  KEY `idx_checkout` (`checked_out`),
  KEY `idx_state` (`state`),
  KEY `idx_catid` (`catid`),
  KEY `idx_createdby` (`created_by`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=8 ;

--
-- Dumping data for table `tbdb_content`
--

INSERT INTO `tbdb_content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES
(1, 'How it Works', 'how-it-works', '', '', '\r\n<div class="article"><span class="subhead">Here is what you need to know.........</span>\r\n<p><a href="#reg"><img class="link-arrow" src="components/com_tournament/images/howtoplay/link-arrow.gif" border="0" alt="&gt;" /> Registration</a><br /> <a href="#how"><img class="link-arrow" src="components/com_tournament/images/howtoplay/link-arrow.gif" border="0" alt="&gt;" /> How it Works</a></p>\r\n<br /><br /> <span class="title-img"><a name="reg"><img src="components/com_tournament/images/howtoplay/how-to-register.gif" border="0" alt="How to register" /></a></span> <span class="subhead">1. Simply click on the "<a href="user/register">Create an account</a>" link at www.topbetta.com.</span> <span class="article-img"><img src="components/com_tournament/images/howtoplay/create-account-example.jpg" border="0" alt="create account example" /></span> <span class="subhead">2. Enter your details</span>\r\n<p>Note: Your username will be the name everyone on TopBetta knows you as!</p>\r\n<span class="subhead">3. Verification</span>\r\n<p>Once you hit the "Create Account" button, we send you a verification email to ensure you are         you!<br /> Click on the link in the email and you are good to go.</p>\r\n<p>You should now see the Home Page</p>\r\n<br /><br /> <span class="title-img"><a name="how"><img src="components/com_tournament/images/howtoplay/how-to-play.gif" border="0" alt="How to play" /></a></span>\r\n<p>The betting takes place across horse, harness and dog races.</p>\r\n<p>The Tournaments are either single or jackpot tournaments.</p>\r\n<p>A single tournament features races across a single race meeting and pays out cash prizes.</p>\r\n<p>A jackpot tournament is made up of a series of designated meetings, each of which awards tickets to subsequent rounds and then to a final offering a guaranteed cash Jackpot prize pool.</p>\r\n<p>We have free jackpot tournaments (i.e. ones that are free to enter), as well as cash tournaments where entry fees are required (90% of this entry fee goes straight into the tournament prize pool).</p>\r\n<p>Upon entry to a tournament we give you $1,000 in "Bettabucks" (fantasy dollars) to bet on the races in this Tournament. You select a runner and bet for an outright win or for a place.</p>\r\n<p>The most profitable punters (i.e. the ones who have the most Bettabucks at the end of the tournament) will win the prizes. Note: You must bet your entire starting allotment of Bettabucks to qualify for a prize!</p>\r\n<p>The Cash and Jackpot Tournament pages will be your entry point for all the races. You can keep an eye on how you are going via the Leader Board.</p>\r\n<p>When you win a ticket into the next round of a jackpot tournament, we''ll send you an email with the details.</p>\r\n<p>If you don''t win a ticket into the final round (or any of the earlier rounds) all is not lost! You can enter another free tournament; in factm there is no limit to the number of free tournaments you can enter. Alternatively you can ''buy in'' to any of our cash tournaments. (The ''buy in'' price for a Tournament is clearly marked.)</p>\r\n<p>If you need some help placing a bet, <a href="content/article/6">click here</a> - Betting Guide.</p>\r\n<p>Or for more information please read our Terms and Conditions <a href="content/article/2">click here</a>.</p>\r\n</div>', 1, 0, 0, 0, '2009-08-19 23:33:55', 62, '', '2010-11-28 23:21:26', 62, 0, '0000-00-00 00:00:00', '2009-08-19 23:33:55', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=', 16, 0, 7, '', '', 0, 164, 'robots=\nauthor=');
INSERT INTO `tbdb_content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES
(2, 'Terms and Conditions', 'terms-conditions', '', '', '<div class="article">\r\n\r\n<span class="subhead">For <strong>Privacy Policy</strong>, <a href="#privacy">click here</a>.</span>\r\n\r\n<span class="subhead">1 TopBetta - Licensing and general terms</span>\r\n\r\n<p style="padding-left: 10px">\r\na.	These Terms and Conditions apply to your use of the TopBetta website and the services provided by TopBetta Pty Ltd, ABN 87 132 843 817, a company registered in Australia under the Corporations Act 2001 (Cth), via the website ("Services").  By using the TopBetta website, becoming a Member or placing a bet via the website you indicate your agreement to be bound by these Terms and Conditions, forming a binding agreement between you and TopBetta Pty Ltd.  TopBetta is fully licensed in Norfolk Island, Australia by the Norfolk Island Gaming Authority to accept bets via the internet.<br /><br />\r\nb.	These Terms and Conditions are governed by and will be construed in accordance with the laws in force in Norfolk Island, Australia. Each party irrevocably and unconditionally submits to the jurisdiction of the courts of Norfolk Island, Australia, and any courts which have jurisdiction to hear appeals from the courts of Norfolk Island, and waives any right to object to any proceedings being brought in those courts.<br /><br />\r\nc.	TopBetta grants you a limited licence to access this site provided you comply at all times with these Terms and Conditions.<br />\r\nd.	The availability of the TopBetta site does not constitute an offer or invitation by TopBetta to you to use the site in any jurisdiction in which such use is illegal.<br /><br />\r\ne.	A Member may only assign its rights under these Terms and Conditions with the prior written consent of TopBetta.  TopBetta may assign any or all of  it rights or obligations under these Terms and Conditions to any third party at any time without notice to you.<br />\r\nf.	Any term by its nature that is intended to survive termination of hese Terms and Conditions survives termination of these Terms and Conditions.<br /><br />\r\ng.	A term or part of a term of these Terms and Conditions that is illegal or unenforceable in any jurisdiction may be severed from these Terms and Conditions for the purposes of that jurisdiction only and the remaining terms or parts of the terms of these Terms and Conditions continue in force.<br /><br />\r\nh.	TopBetta reserve the right to make changes to the site and any guides and policies included on the site, including these Terms and Conditions, at any time. You will be subject to the guides, policies, conditions and agreements in force at the time that your bets are accepted. It is therefore your responsibility to check the guides, policies and these Terms and Conditions from time to time to ensure that you agree with them, and your continued use of the site and/or the Services will be deemed to be your acceptance of any changes we may make.<br /><br />\r\ni.	These Terms and Conditions and the terms expressly incorporated into them constitute the entire and only agreement between the parties with regards to its subject matter and each party confirms that it has not been induced to accept these Terms and Conditions in reliance upon, nor has it been given, any warranty (including in particular any warranty as to merchantability, fitness for purpose or uninterrupted functionality), representation, statement, assurance, covenant, agreement, undertaking, indemnity or commitment of any nature whatsoever other than as are expressly set out in these Terms and Conditions and, to the extent that it has been, it unconditionally and irrevocably waives any claims, rights or remedies which it might otherwise have had in relation thereto.<br /><br />\r\nj.	No failure or delay by a party to exercise any of its rights under these Terms and Conditions shall operate as a waiver thereof and no single or partial exercise of any such right shall prevent any other or further exercise of that or any other right.\r\n</p>\r\n\r\n<span class="subhead">2 Definitions</span>\r\n<p>Definitions in these Terms and Conditions:</p>\r\n<p>The <i><strong>Authority</strong></i> is the Norfolk Island Gaming Authority.</p>\r\n<p><i><strong>Displayed Odds</strong></i> is an indicative price quoted by TopBetta to the Member at the time of placing a bet.</p>\r\n<p>An <i><strong>Event</strong></i> is a race, game or tournament that is offered by TopBetta for the purposes of wagering.</p>\r\n<p><i><strong>Late Scratching</strong></i> means a horse, dog etc, is withdrawn late by order of the stewards.</p>\r\n<p>A <i><strong>Member</strong></i> is a person that is at least 18 years of age and holds a Member Betting account with the TopBetta.</p>\r\n<p><i><strong>TopBetta, we</strong></i> or <i><strong>us</strong></i> means TopBetta Pty Ltd (ABN 87 132 8843 817)..</p>\r\n\r\n<span class="subhead">3 Contact Us</span>\r\n<p>Communications and notices to be given by you to us under these Terms and Conditions (other than those exchanges of information occurring in the normal operation of the site) must be provided to us as set out in the <a href="index.php?option=com_contact&view=contact&id=1&Itemid=10">Contact us</a> section on the TopBetta site.</p>\r\n<p>Any communication with TopBetta via telephone is recorded as required by government regulations. These recordings provide protection to you and TopBetta in the event of a dispute. They also enable regulatory authorities to carry out their respective duties.  Similarly, the content of all bets and betting requests are stored on the TopBetta computer system (and a copy provided to the regulator).</p>\r\n\r\n<span class="subhead">4	TopBetta - Use of Our Services</span>\r\n<p style="padding-left: 10px"><strong>4.1 Undertakings</strong><br /><br />\r\n4.1.1	Your use of this site or any data feeds we may provide to you confers no rights whatsoever to the content or related intellectual property rights subsisting in the site or in any such data feeds. You agree not to monitor, use or copy our web pages or any of our web content, including without limitation any price data. You will not attempt to hack, make unauthorised alterations or introduce any kind of malicious code to the site by any means. You will not (a) reverse engineer or decompile (whether in whole or in part) any software available through the site; or (b) make copies, modify, reproduce, transmit, alter or distribute all or any part of the site or any material or information contained on it. Any unauthorised use or reproduction may be prosecuted.<br /><br />\r\n4.1.2	You understand that you may lose money on bets placed and tournament entry fees paid and accept that you are fully responsible for any such loss. You accept that under no circumstances will any amounts lost by you under any bet be recoverable from us.<br /><br />\r\n4.1.3	It is your responsibility to verify that you are permitted to bet with us in any jurisdiction in which you are located and you agree that you will not bet with us while you are located in a jurisdiction which prohibits you from betting with us. In particular, you will not bet or attempt to bet with us if you are located in the United States of America or such other restricted jurisdictions as TopBetta may from time to time determine.<br /><br />\r\n4.1.4	You undertake to use our services for legitimate betting purposes only. In particular you undertake not to (i) engage in any activity which has the purpose or effect of causing damage to or in any way hindering our business operations or generating or increasing any liability on us, including without limitation any tax, levy or duty; or (ii) bet on or manipulate any individual Event in a manner which we believe has the purpose or effect of adversely affecting the integrity of the Event.<br /><br />\r\n4.1.5	To use this site correctly you must ensure that your browser is set to accept "cookies" and your browser must support JavaScript.</p>\r\n\r\n<span class="subhead">5	TopBetta – Your Conduct</span>\r\n<p style="padding-left: 10px"><strong>5.1 Undertakings</strong><br /><br />\r\n5.1.1	You will not attempt to use the site to deliberately transfer money from one TopBetta account to another by attempting to match bets on an event with another account through collusion.<br /><br />\r\n5.1.2	You will inform us as soon as you become aware of any errors in calculations with respect to any betting transaction. We reserve the right to declare null and void any bets which are the subject of such an error.<br /><br />\r\n5.1.3	You will not disguise or interfere in any way with the IP address of the computer you are using to access the site or otherwise take steps to prevent us from correctly identifying the actual IP address of the computer you are using whilst accessing the site.<br /><br />\r\n5.1.4	You agree it is your responsibility to account for any tax or duty imposed on you as a result of any of your betting transactions.<br /><br />\r\n5.1.5	You agree that you will not access any other TopBetta account to use any of our services or products from which you have self-excluded, during the period of any such self-exclusion.</p>\r\n<p style="padding-left: 10px">To commence a self-exclusion period a Player must either:\r\n<ul>\r\n<li>email help@topbetta.com with their account details and period of their self-exclusion. On receipt of a self-exclusion request, the member will be blocked from accessing the site. The self-exclusion period cannot be cancelled or shortened.  The self-exclusion period may however be extended at any time; or</li>\r\n<li>click on the “Self Exclusion” button located on the TopBetta site (www.topbetta.com), the member will be logged out of TopBetta immediately for a period of seven days and an email will be sent to the member confirming their self exclusion.</li>\r\n</ul></p>\r\n\r\n<p style="padding-left: 10px">5.1.6	You will not make offensive comments, use offensive or pornographic material or make potentially defamatory or inflammatory remarks in connection with any part of our site including within your own username or in other information contributed to our site.</p>\r\n\r\n<span class="subhead">6	Members Responsibility</span>\r\n<p style="padding-left: 10px"><strong>6.1 Minimum age and Personal Information</strong><br /><br />\r\n6.1.1	The minimum age to open an account with TopBetta is eighteen (18) years. TopBetta does not accept responsibility for under-age applicants.<br /><br />\r\n6.1.2	TopBetta can refuse to pay out a bet if we believe a client or player is under eighteen (18) years of age, or a client or player makes a bet on behalf of a person who is under eighteen (18) years of age.<br /><br />\r\n6.1.3	You warrant that you are eighteen (18) years of age or older, that the name and address you supplied when opening your account are correct and that you are the owner of the sums which are deposited in your account. You agree that we may use personal information provided by you in order to conduct appropriate anti fraud checks. Personal information that you provide may be disclosed to a credit reference or fraud prevention agency, which may keep a record of that information.<br /><br />\r\n6.1.4	If we cannot verify your details electronically, including but not limited to verifying your age or identity (which we are legally obliged to verify), we will contact you and ask you to provide further information to us directly.<br /><br />\r\n6.1.5	If we are unable to verify your identification details within 90 days of your account being opened then we will suspend your account, as required by law.<br /><br />\r\n6.1.6	No funds will be returned to you until identification acceptable to us has been provided.<br /><br />\r\n6.1.7	It is the member’s responsibility to ensure that their personal details especially address, telephone number and payment/bank details are kept up to date. In the event that a member does not keep their personal details up to date TopBetta reserves the right to suspend the account.<br /><br />\r\n6.1.8	Members will be responsible for all wagers in which their member ID and /or password are used.<br /><br />\r\n6.1.9	Any bet (wager) made via the internet where a valid member ID and password have been used will be regarded by TopBetta as being valid and may not be changed, cancelled or modified.<br /><br />\r\n6.1.10	In accordance with Norfolk Island Government licensing regulations, all members must have their identification verified.<br /><br />\r\n6.1.11	All credit cards must be in the name of the TopBetta member.<br /><br />\r\n6.1.12	Where the account holder resides outside of Australia, they will be required to fulfil ID requirements prior to their account being activated.<br /><br />\r\n6.1.13	Under the terms of our Norfolk Island license, where an account is suspended, all bets will stand.<a name="privacy" id="privacy">&nbsp;</a></p>\r\n\r\n<p style="padding-left: 10px"><strong>6.2 Personal Information and Privacy</strong><br /><br />\r\n6.2.1	Personal information of a Member is any information about you where your identify is apparent or can reasonably be ascertained from the information.  Personal information we may collect about Members may include, without limitation, a Member''s:</p>\r\n<p style="padding-left: 30px">a.	name;<br />\r\nb.	address;<br />\r\nc.	bank account/ credit card details;<br />\r\nd.	betting records; and<br />\r\ne.	ISP address.</p>\r\n<p style="padding-left: 10px">6.2.2	TopBetta will collect, store and disclose Members'' personal information in accordance with the requirements of the Privacy Act 1988 (Cth).<br /><br />\r\n6.2.3	The type of personal information that TopBetta collects from its members will depend on the dealings the Member has with TopBetta, the requirements of any licenses TopBetta holds, the accompanying conditions that apply to those licenses, and the laws and regulations that apply to TopBetta''s business, including, but not limited to, the Anti Money Laundering and Counter Terrorism Financing Act 2006.<br /><br />\r\n6.2.4	The Member warrants and represents that any personal information supplied by the Member to TopBetta is true, correct and complete and will remain so.<br /><br />\r\n6.2.5	A Member will, upon request by TopBetta establish the truth, correctness and completeness of any of the Member''s personal Information.<br /><br />\r\n6.2.6	TopBetta will take reasonable steps to:</p>\r\n<p style="padding-left: 30px">6.2.6.1	make sure that the personal information it collects, uses or discloses is accurate, complete and up to date;<br /><br />\r\n6.2.6.2	protect the personal information it holds from misuse and loss and from unauthorised access, modification or disclosure; and<br /><br />\r\n6.2.6.3	destroy or permanently de-identify personal information if it is no longer needed for any purpose for which the information may be used or disclosed under the Privacy Act 1988 (Cth).</p>\r\n<p style="padding-left: 10px">6.2.7	The Member acknowledges and agrees that all personal information held by TopBetta may be used or disclosed by TopBetta as set out below and as permitted by the Privacy Act 1988 (Cth). Without limitation, a member''s personal information may be collected, stored, used or disclosed:</p>\r\n<p style="padding-left: 30px">6.2.7.1	to enable TopBetta to perform its obligations to the member under these Terms and Conditions;<br /><br />\r\n6.2.7.2	to enable TopBetta to ensure that the member performs its obligations under these Terms and Conditions;<br /><br />\r\n6.2.7.3	 to a credit reporting agency;<br /><br />\r\n6.2.7.4	 to maintain a credit information file about the member;<br /><br />\r\n6.2.7.5	 to carry out TopBetta''s own credit assessment on the member;<br /><br />\r\n6.2.7.6	for planning, research, promotion and marketing of TopBetta products and services;<br /><br />\r\n6.2.7.7	to organisations associated with the provision of TopBetta''s services, such as associated data centres, including for marketing and analysis purposes;<br /><br />\r\n6.2.7.8	to a potential purchaser of, or investor or in, any business conducted by TopBetta;<br /><br />\r\n6.2.7.9	where TopBetta is required to do so by law or where the disclosure is reasonably necessary to enforce the law.<br /><br />\r\n6.2.7.10	to assist any other government agencies with enquiries made under state or federal legislation, for example Child Support Agencies;<br /><br />\r\n6.2.7.11	to comply with TopBetta''s disclosure obligations to the racing industries and authorised sporting bodies in accordance with TopBetta''s contractual obligations to those state racing industries and authorised sporting bodies;<br /><br />\r\n6.2.7.12	to comply with all obligations that TopBetta has under its licence conditions, laws and regulations, including, but not limited to the Anti-Money Laundering and Counter-Terrorism Financing Act 2006 (Cth);<br /><br />\r\n6.2.7.13	to assist a law enforcement agency or any government agency responsible for national security in the performance of its functions; and<br /><br />\r\n6.2.7.14	to protect TopBetta''s rights or property or those of any other website or TopBetta service user, or any member of the public.<br /></p>\r\n<p style="padding-left: 10px">6.2.8	The member is welcome to request access to any personal information of the member that TopBetta holds by contacting TopBetta. <br /><br />\r\n6.2.9	There are circumstances in which TopBetta is not required to give a member access to its personal information. TopBetta will let the member know if one of these exceptions applies to the member''s request. <br /><br />\r\n6.2.10	If TopBetta intends to charge the member a fee for TopBetta providing the member with a copy of the personal information TopBetta holds on that member, TopBetta will inform the member of the cost before providing the member with the information. <br /><br />\r\n6.2.11	If the member does not, or refuses to, provide TopBetta with any personal information that TopBetta reasonably requires, TopBetta may, at TopBetta ''s discretion, suspend or close any account the member holds with TopBetta. <br /><br />\r\n6.2.12	The Member acknowledges and agrees that they may only hold one TopBetta account.</p>\r\n\r\n<p style="padding-left: 10px"><strong>6.3 Participants in Events</strong><br /><br />\r\n6.3.1	Members who are participants in, or are associates of participants in, certain events ("Relevant Events"), are likely to be subject to the rules, regulations, codes, agreements and/or guidelines made by the sporting body or racing industry that administers those events ("the Rules").<br /><br />\r\n6.3.2	If a Member is subject to the Rules, it may only place wagers in accordance with those Rules. If those Rules preclude the Member from betting on the Relevant Events or any aspects of the Relevant Events, the Member agrees that it will not bet on those Relevant Events or aspects of those Relevant Events (as applicable).<br /><br />\r\n6.3.3	The Member acknowledges and agrees that TopBetta will provide information to sporting bodies, racing industries and government authorities authorised to issue licences for the conduct of betting services and/or responsible for supervising the betting operations of TopBetta ("Relevant Authorities"), in respect of persons:</p>\r\n<p style="padding-left: 30px">6.3.3.1	who are precluded by the Rules from betting on any Relevant Events or aspects of any Relevant Events;<br /><br />\r\n6.3.3.2	whom TopBetta or any authority has reasonable grounds to suspect is connected to a Member and TopBetta  or any Relevant Authority has reasonable grounds to suspect that that person or the Member is in breach of, the Rules;<br /><br />\r\n6.3.3.3	whom TopBetta or any Relevant Authority has reasonable grounds to suspect have engaged in conduct prejudicial to the interests of TopBetta or the Relevant Authority or who may have relevant knowledge concerning corruption within the relevant sport; and<br /><br />\r\n6.3.3.4	whom TopBetta or any Relevant Authority believes have made unusual or suspicious bets or have unusual or suspicious betting trends or movements or other information leads TopBetta or the Relevant Authority to query the integrity of any of the Relevant Events, or a person subject to the Rules.</p>\r\n<p style="padding-left: 10px">6.3.4	In addition, a close associate of TopBetta is precluded from betting.\r\nFor the purposes of these Terms and Conditions two persons are “close associates” if:</p>\r\n<p style="padding-left: 30px">6.3.4.1	one is a spouse, parent, brother, sister or child of the other; or<br /><br />\r\n6.3.4.2	they are members of the same household; or<br /><br />\r\n6.3.4.3	they are in partnership or are joint ventures; or<br /><br />\r\n6.3.4.4	one is a body corporate and the other is a director or executive officer of the body corporate; or<br /><br />\r\n6.3.4.5	one is a body corporate (other than a body corporate whose shares are listed on a stock exchange) and the other is a shareholder in the body corporate; or<br /><br />\r\n6.3.4.6	one is a body corporate whose shares are listed on a stock exchange and the other is a substantial shareholder in the body corporate: or<br /><br />\r\n6.3.4.7	one has a right to participate (otherwise than as a shareholder in a body corporate) in the income or profits derived from a licensee’s licensed operations and the other is the licensee: or<br /><br />\r\n6.3.4.8	one provides:<br /><br />\r\na. goods; or<br /><br />\r\nb. services (whether as an employee or otherwise),<br /><br />\r\nc. to other in respect of licensed operations (other than goods or services prescribed for the purposes of this paragraph); or<br /><br />\r\nd. one is in a position to exercise control or significant influence over the conduct of the other; or<br /><br />\r\ne. a chain of relationships can be traced between them under any one or more of the paragraphs of this section.\r\n</p>\r\n\r\n<span class="subhead">7	Conditions Relating to Betting</span>\r\n<p style="padding-left: 10px"><strong>7.1	Operation of our Services</strong><br /><br />\r\n7.1.1	You are responsible for understanding the contents of our site and the operation of the Services. We reserve the right to change the format of the Services at any time, including in order to enhance such Services.<br /><br />\r\n7.1.2	You are responsible for understanding the contents of our site and the operation of the Services. We reserve the right to change the format of the Services at any time, including in order to enhance such Services.<br /><br />\r\n7.1.3	We may determine when Events are open for betting and may close the Events at any time in our absolute discretion.<br /><br />\r\n7.1.4	We may take steps to block you from betting on our site if you are located in certain jurisdictions, for example the United States of America.<br /><br />\r\nYou are entirely responsible for the information that you provide to us concerning the offer that you wish to place. We will not enquire into the reasonableness of any offer that you may make. We will not be liable for incorrect entries made by you, including data input errors with respect to the odds, price or stake on offer. <br /><br />\r\n7.1.5	In the interest of maintaining integrity and fairness we may, at our sole and absolute discretion, decide to suspend betting on a Event earlier than anticipated and/or void certain bets or an Event in its entirety.</p>\r\n<p style="padding-left: 10px"><strong>7.2	Cancellation, Termination, Suspension and Breach</strong><br /><br />\r\n7.2.1	We may restrict your access to TopBetta, suspend or terminate your account, withdraw your bets or void or cancel any bets outstanding to your account, in our absolute discretion with or without cause at any time, including if:</p>\r\n<p style="padding-left: 30px">7.2.1.1	there is a technological failure;<br /><br />\r\n7.2.1.2	we suspect that you are engaging in illegal or fraudulent activity;<br /><br />\r\n7.2.1.3	we suspect that you have (or may have) breached any part of these Terms and Conditions;<br /><br />\r\n7.2.1.4	we suspect you are acting in a manner that is detrimental to the conduct of our business or which may result in legal liability for you, us or third parties; or<br /><br />\r\n7.2.1.5	we suspect that you may be having difficulties obtaining credit.</p>\r\n<p style="padding-left: 10px">7.2.2	In relation to any cancelled or void bets, we reserve the right to:</p>\r\n<p style="padding-left: 30px">7.2.2.1	notify the Authority of any actions we have taken ;<br />\r\n7.2.2.2	hold in your account any money relating to these bets, until a determination has been made by the Authority as to how these funds are dispersed.<br />\r\n7.2.2.3	demand that you pay us the relevant outstanding amount relating to these bets if there are insufficient funds in your accounts.</p>\r\n<p style="padding-left: 10px">7.2.3	Should a member wish to cancel their account, this can be done by emailing help@topbetta.com with details. Any existing bets as at the cancellation date will stand and a final payout value will be determined once all bets have been concluded. It will be the responsibility of the member to clear their account.</p>\r\n<p style="padding-left: 10px"><strong>7.3	Gambling by those under age</strong><br /><br />\r\nIf we are notified that you are under 18 or were under 18 when you entered into any betting transactions on our site ("under age"):<br /><br />\r\n7.3.1	we will immediately prevent you from entering into any betting transactions, making any withdrawals from or using your account;<br /><br />\r\n7.3.2	we will investigate the claim that you are or were under age, including whether in fact you have been betting as an agent for or at the behest or on behalf of another person;<br /><br />\r\n7.3.3	if we find that you are or were under age, we will notify the Authority and any other relevant party and  all winnings which have accrued and remain in your account or which are otherwise due to be credited to your account, will remain in your account until a determination has been made by the Authority as to how these funds are dispersed.<br /><br />\r\n7.3.4	you must pay to TopBetta all winnings gained from betting whilst under age on demand. Should you not comply with this duty we will seek to recover from you or to set off against any stakes placed by you any winnings paid to you as a result of bets placed together with the costs of such recovery. Any sums recovered will belong to TopBetta; <br /><br />\r\n7.3.5	we will not return to you any charges paid by you to us in respect of bets made whilst you were under age; and<br /><br />\r\n7.3.6	any winnings you may make through your account shall be forfeited to the Authority.</p>\r\n<p style="padding-left: 10px"><strong>7.4	Bet Placement and Acceptance</strong><br /><br />\r\n7.4.1	All bets are placed and received in Norfolk Island in the Commonwealth of Australia and accordingly all betting transactions are formed in Norfolk Island.<br /><br />\r\n7.4.2	Bets will be accepted up to the advertised start time of the Event or such earlier time as dictated by TopBetta specified on the website. If a bet is inadvertently accepted in respect to an Event after its start time, the bet shall be deemed to have been made invalidly and will be refunded to the Member.<br /><br />\r\n7.4.3	If an Event is abandoned or postponed and not completed within 24 hours of its scheduled completion, all bets will be void and wagers refunded to the Member.<br /><br />\r\n7.4.4	When making a wager via the internet a bet is accepted when the Member receives a “Confirmation” message after the bet is processed through the TopBetta service.</p>\r\n<p style="padding-left: 10px"><strong>7.5	Bet Amendments and Changes</strong><br /><br />\r\n7.5.1	Postponed - If a race meeting is postponed, all wagers on the postponed meeting are cancelled. <br /><br />\r\n7.5.2	Scratching - Bets struck after the final field is declared are subject to refunds on scratched horses, and deductions may apply on remaining runners. The value of the deduction is determined by the price of the scratched horse as declared by the principal club. <br /><br />\r\n7.5.3	Starting Time - All interstate and country wagers are strictly subject to time. If an interstate or country wager is accepted, but afterwards found to have been taken after the actual starting time, the bet will be voided and refunded.<br /><br />\r\n7.5.4	Transferred - Where a race meeting is transferred from one racecourse to another, all betting rules will apply as if the meeting was not transferred.</p>\r\n\r\n<p style="padding-left: 10px"><strong>7.6	Cancellation of a bet</strong><br /><br />\r\nOnce placed and accepted, a bet may not be altered or cancelled.</p>\r\n<p style="padding-left: 10px"><strong>7.7	Minimum and maximum bet stakes </strong><br /><br />\r\nDepending on the product or type of Event you are betting on and depending on the currency that you are betting in, there are minimum bet sizes that apply. These minimum bet sizes are subject to change and may differ. You are not permitted to place bets or try to place bets that are below the minimum bet size threshold and doing so may result in your account being permanently closed. The maximum bet size is $100 per bet. </p>\r\n<p style="padding-left: 10px"><strong>7.8	Displayed Odds</strong></br /><br />\r\nThe TopBetta odds displayed are totaliser odds.<br />\r\nAs winnings on totaliser betting are determined by dividing the entire pool invested amongst those who win, we are unable to provide exact odds prior to the off, or on an Event being suspended. However, for your benefit we will provide indicative odds for events, in the form of a win or a place.</p>\r\n<p style="padding-left: 10px"><strong>7.9	Bet Settlement</strong><br /><br />\r\nSubject to these Terms and Conditions and any specific conditions applicable in respect of a particular Event, we will settle Events in accordance with these Terms and Conditions. We reserve the right to reverse or amend settlement in the event of an Event being settled incorrectly. This may lead to amendments being made to your account to reflect changes in the Event settlement and if there are insufficient funds in your account we may demand that you put funds into your account to address the outstanding balance.<br />\r\nWinnings will be credited to a Member’s account following confirmation of the final result. In the event :<br /><br />\r\n7.9.1	That an incorrect balance is provided by the Member, it is the Member’s responsibility to notify TopBetta of the error without delay. <br /><br />\r\n7.9.2	That funds or bets are credited to a Member’s account in error by TopBetta, it is the Member’s responsibility to notify TopBetta of the error without delay and the Member shall return to TopBetta any funds credited in error.</p>\r\n<p style="padding-left: 10px"><strong>7.10	Unclaimed Prize Remittances</strong><br /><br />\r\nIf a prize or winnings are not claimed within 2 years after the start time of the event in which the prize/winnings was won, the entitlement to the prize or winnings is forfeited to the Authority.</p>\r\n<p style="padding-left: 10px"><strong>7.11	Dishonoured Payments</strong><br /><br />\r\nIf a credit or debit card payment is dishonoured at any stage after TopBetta receives authorisation from the relevant financial institution:<br /><br />\r\n7.11.1	all winnings which have accrued and remain in your account or which are otherwise due to be credited to your account shall be suspended until such time that the dishonoured payment is honoured.<br /><br />\r\nIf the payment remains dishonoured:<br /><br />\r\n7.11.2	all winnings which have accrued and remain in your account or which are otherwise due to be credited to your account that relate to the dishonoured payment will remain in the account and shall be forfeited to the Authority; and<br /><br />\r\n7.11.3	you must pay to TopBetta all winnings gained from the dishonoured payment(s). Should you not comply with this duty we will seek to recover from you or will set off against any stakes placed by you, any winnings paid to you as a result of bets placed together with the costs of such recovery.  Any sums recovered will belong to TopBetta.</p>\r\n<p style="padding-left: 10px"><strong>7.12	Rounding</strong><br /><br />\r\nAll bets and winnings relating to a bet on an Event will be rounded to the nearest cent.<br />\r\nWhere required a bet will be rounded before it is accepted. For winnings, the rounding will take place after the payout has been calculated. Any fractions of a cent will be rounded to the nearest cent, with fractions greater than or equal to 0.5 being rounded up.</p>\r\n\r\n<span class="subhead">8	Conditions Relating to TopBetta</span>\r\n<p style="padding-left: 10px"><strong>8.1	Deposits</strong><br /><br />\r\n8.1.1	Deposits to a Member''s account may be made via Credit or Debit Card, PayPal, Bpay or direct bank deposit. A member must abide by the respective rules set by the providers of these deposit mechanisms.<br /><br />\r\nWeb Active Corporation Pty Ltd, trading as eWAY, is responsible for management of Credit and Debit Card payments and credits in relation to the Services. For further information please refer to www.eway.com.au. TopBetta does not possess or retain any information relating to a Member''s credit or debit card information.</p>\r\n<p style="padding-left: 10px"><strong>8.2</strong></p>\r\n<p style="padding-left: 10px"><strong>8.3	Withdrawals</strong><br /><br />\r\n8.3.1	Withdrawals from Member betting accounts are governed by the Anti-Money Laundering and Counter-Terrorism Financing Act 2006 (Cth)and as a result certain conditions may apply. These may include but are not limited to:</p>\r\n<p style="padding-left: 30px">8.3.1.1	A withdrawal being made via the last method of deposit<br /><br />\r\n8.3.1.2	A withdrawal being restricted by time<br /><br />\r\n8.3.1.3	A withdrawal being restricted by account activity<br /><br />\r\n8.3.1.4	A withdrawal being restricted by value<br /><br />\r\nFor further information please refer to the legislation.<br /><br />\r\nWithdrawals are normally processed within seven days.</p>\r\n<p style="padding-left: 10px"><strong>8.4	Matters beyond our reasonable control </strong><br /><br />\r\n8.4.1	We are not liable for any loss or damage that you may suffer because of any: act of God; power cut; trade or labour dispute, act, failure or omission of any government or authority; obstruction or failure of telecommunication services; or any other delay, Service interruption or failure outside of the control of TopBetta. In such an event, we reserve the right to cancel or suspend our services without incurring any liability.<br /><br />\r\n8.4.2	We are not liable for the failure of any equipment or software howsoever caused, wherever located or administered, whether under our direct control or not, that may prevent the operation of the Services, impede the placing of offers for bets or the matching of bets, or prevent you from being able to contact us. In particular you should be aware that if you place a bet with the intention of subsequently placing a separate bet to reduce the liability incurred by the initial bet, there can never be a guarantee that the Services will be available at the point you wish to place the subsequent bet.<br /><br />\r\n8.4.3	We will not be liable for any failure to perform by a third party.</p>\r\n<p style="padding-left: 10px"><strong>8.5	Indemnity</strong><br /><br />\r\n8.5.1	You agree to indemnify and hold us and our associated companies, affiliates, officers, directors, agents and employees harmless from any liabilities, claims, losses or demands made by any third party arising out of your breach of these Terms and Conditions (including documents incorporated by reference) or out of your violation of any law or the rights of any third party.<br /><br />\r\n8.5.2	You acknowledge that TopBetta and its affiliates will hold information with respect to your identity, including but not limited to your name, address and payment details. You agree that we rely on this information in entering into these Terms and Conditions and you agree to hold us harmless against any falsehood or inaccuracy contained in the information you provide us.</p>\r\n<p style="padding-left: 10px"><strong>8.6	Limitation of liability</strong><br /><br />\r\n8.6.1	To the extent permitted by law, we exclude all representations and warranties as to the satisfactory quality and/or fitness for its intended purpose and/or accuracy and completeness of the Services.<br /><br />\r\n8.6.2	We will not be liable to you for any loss that you may incur as a result of any loss or misuse of your password, and we accept no liability resulting from its unauthorised use, whether fraudulent or otherwise.<br /><br />\r\n8.6.3	To the extent permitted by law, in no event will we, or any of our suppliers, accept any liability however arising for any losses you may incur as a result of using the Services. Under no circumstances will our liability under or in connection with these Terms and Conditions, whether arising for breach of contract, negligence or other tort, equity or otherwise exceed the amount of the relevant bet placed by you. Under no circumstances will we be liable for any indirect, special or consequential damages, loss of profits (direct or indirect) or loss of the benefit of any bet, whether such loss arises from breach of contract, negligence or other tort, equitable duty (including for the avoidance of doubt in relation to any bet(s) or Event(s) voided by us) or otherwise even if we had been advised of or known (or should have known) of the possibility of such damages or loss.<br /><br />\r\n8.6.4	Nothing in these Terms and Conditions excludes our liability with respect to death and personal injury resulting from our negligence, or that of our employees, agents or subcontractors.</p>\r\n<p style="padding-left: 10px"><strong>8.7	Right of set off</strong><br /><br />\r\nWe may at any time, without notice to you, notify the Authority and set off any liability owed to us by you, including (without limitation) in relation to any TopBetta account in your name or which we believe to be under your control, against any funds held in another TopBetta account in your name or which we believe to be under your control in accordance with the terms of the Agreement.</p>\r\n<p style="padding-left: 10px"><strong>8.8	Intellectual property</strong><br /><br />\r\n8.8.1	TopBetta is the owner of:</p>\r\n<p style="padding-left: 30px">8.8.1.1	all copyright and related rights in and to our site, including, without limitation, rights in databases and all rights in any price data and related content on our site except for certain third party rights;<br /><br />\r\n8.8.1.2	all trade mark rights, whether registered or unregistered, in and to TopBetta and the TopBetta logo; and <br /><br />\r\n8.8.1.3	the domain name www.TopBetta.com which is our uniform resource locator ("URL").</p>\r\n<p style="padding-left: 10px">8.8.2	Any unauthorised use of any of these rights may result in prosecution or other action being taken against you<br /><br />\r\n8.8.3	You hereby assign to TopBetta absolutely, any and all copyright and other intellectual property rights throughout the world in all media whether now known or hereafter developed, for the full period of copyright, including by way of present assignment of future copyright and all other rights whatsoever, in any offers for bets or bets placed by you on our site.<br /><br />\r\n8.8.4	Any data licensed to TopBetta is provided for use on our site only and may not be used for any commercial purposes without the consent of such third parties.</p>\r\n<p style="padding-left: 10px"><strong>8.9	Dispute Resolution</strong><br /><br />\r\n8.9.1	TopBetta will attempt to resolve any disputes or complaints that you may have in regard to our products or service. If you wish to raise any issue with us, then please email TopBetta using the help@topbetta.com email address.<br /><br />\r\n8.9.2	If you have a betting or gaming dispute in relation to one of our products or services which you believe that TopBetta has not satisfactorily resolved, you can approach the Norfolk Island Gaming Authority, this approach should be within 14 days of the end of the Event for which the bet was made. The mailing address is:<br /><br />\r\nThe Director<br />\r\nNorfolk Island Gaming Authority<br />\r\nP.O. Box 882<br />\r\nNorfolk Island 2899<br />\r\nSouth Pacific<br /><br />\r\n8.9.3	Any decision of the Gaming Authority is final and binding on both parties.</p>\r\n\r\n<span class="subhead">9	Conditions Relating to Other Services</span>\r\n<p style="padding-left: 10px"><strong>9.1	Information Services</strong><br /><br />\r\n9.1.1	From time to time TopBetta may provide you with access to various information and content (e.g. form data, results,) via our site (as part of any runner data and/or results service), emails or any other means of communication. Further TopBetta may provide you with links to third party websites which may include information.<br /><br />\r\n9.1.2	This information is supplied "as is" and for guidance only. Use of such information is entirely at your own risk. To the fullest extent permitted by law, TopBetta makes no representations or warranties of any kind, including but not limited to, the accuracy, quality or completeness of the information.<br /><br />\r\n9.1.3	TopBetta shall not be liable for any action taken by you as a result of your relying on any of this information or for any loss or damage suffered by you as a result of you using such information or any third party website linked to TopBetta.<br /><br />\r\n9.1.4	Any links to third party websites do not constitute an endorsement by TopBetta of any products or services available on such websites. You use such websites at your own risk and TopBetta takes no responsibility for the content on, or use of, such websites.</p>\r\n<p style="padding-left: 10px"><strong>9.2	Currency</strong><br /><br />\r\n9.2.1	There are three separate currencies operating in relation to the Service:<br /><br />\r\n9.2.1.1	<br /><br />\r\n9.2.1.2	Cash & Account Balance<br />\r\n“Account Balance” refers to a Member''s actual cash funds,  which are eligible for withdrawal provided the relevant aspects of the Terms and Conditions have been satisfied. The Account Balance is funded by deposits (PayPal, credit card, bank account, or BPAY) and also from cash prize winnings.<br /><br />\r\n9.2.1.3	Tournament Dollars<br />\r\n“Tournament Dollars“ are funds used to enter tournaments on the TopBetta site. Tournament Dollars can be purchased with money from a user’s Account Balance. Tournament Dollars may also be granted by the system as part of a promotion or as a reward for referring a friend; as a prize given for a tournament; or as a refund given when a user withdraws from a tournament. Tournament Dollars are non-redeemable for cash.<br /><br />\r\n9.2.1.4	Bettabucks<br />\r\n“BettaBucks” are the “fantasy dollars” given when playing in a tournament. Upon entering a tournament, each player will be given an allotment of Bettabucks to play with. Bettabucks are only valid for the tournament in relation to which they are given and may not be transferred or withdrawn at any time. Bettabucks are non-redeemable for cash or Tournament Dollars.</p>\r\n<p style="padding-left: 10px"><strong>9.3	Tournaments</strong><br /><br />\r\n9.3.1	A "Tournament" is a group of predefined races or events.</p>\r\n<p style="padding-left: 30px">9.3.1.1	A Single Round Tournament is made up of a group of events occurring on the same day at the same venue i.e. races over the course of a single race meeting.<br /><br />\r\n9.3.1.1	A Jackpot Tournament is a Tournament made up of a number of predefined Single Round Tournaments that are in a sequential order. Entry to each stage is achieved by either<br /><br />\r\na.	Completing the previous stage and winning an entry to the next stage; or<br /><br />\r\nb.	Paying the required entry fee, in either Tournament Dollars or cash.<br /><br />\r\n9.3.2	 To enter a Tournament, a Participant must:<br /><br />\r\n9.3.2.1	become a registered Member of TopBetta by registering their details via the on-line registration form on the TopBetta Web Site at www.topbetta.com and comply with any identity verification requirements and the TopBetta Terms and Conditions; <br /><br />\r\n9.3.2.2	if the Tournament has any associated fees, pay the required Tournament fees.</p>\r\n<p style="padding-left: 10px">9.3.3	The Tournament Entry Fee is quoted in two components (i.e. $50 + $5). Where the larger component is the Prize component referred to as the ‘buy in’and the smaller is the Administration component.<br /><br />\r\n9.3.4	On receipt of the Entry Fee or on entering a Free Tournament a registered Member will be allocated a quantity of Bettabucks (these are fantasy dollars, they have no monetary value and are nonredeemable for cash or Tournament Dollars).<br /><br />\r\n9.3.5	A Member is permitted to enter each Tournament once only.<br /><br />\r\n9.3.6	Bettabucks are for Tournament use only, no actual monetary compensation will be distributed. They are only used to determine entrants'' finishing positions in the Tournament.<br /><br />\r\n9.3.7	During a Tournament, a Member makes a number of bets using their Bettabucks, betting on the races/events that have been allocated to the respective Tournament.<br /><br />\r\n9.3.8	Whilst the broadcast odds for these races are based on live NSWTAB prices, should the Member’s horse (or dog) place in the race the member will receive the allocated winnings in the form of Bettabucks.<br /><br />\r\n9.3.9	To be eligible to win a Tournament prize, a Member must make bets in the respective Tournament equivalent to or greater than their initial allocation of BettaBucks. i.e. if the Member received $1,000 of Bettabucks then bets equal to or greater than $1,000 of Bettabucks must be placed. This bet could be a single bet of $1,000 on a single race or a number of smaller bets across a number of races.<br /><br />\r\n9.3.10	Members can unregister themselves from any Tournament, provided a bet has not been placed and the first race/event has yet to start. A Member will receive a Tournament Dollars credit to the value of their Entry Fee.<br /><br />\r\n9.3.11	Once a Tournament is open it shall remain open  until the start time of the last race/event in that tournament.  <br /><br />\r\n9.3.12	A bet in a Tournament will be accepted up to the advertised start time of the specific Event in the Tournament or such earlier time as dictated by TopBetta specified on the website. If a bet is inadvertently accepted in respect to an Event after its start time, the bet shall be deemed to have been made invalidly and the Bettabucks refunded to the Member.<br /><br />\r\n9.3.13	If a Tournament has to end before all races have been run (i.e. it gets washed out) the Tournament will be cancelled and all Entry Fees will be credited (as Tournament Dollars) to the Members.<br /><br />\r\n9.3.14	If a particular race/event in a Tournament is postponed for more than 6 hours from its scheduled start time, or is cancelled, it will no longer be a part of the Tournament and all Bettabuck bets on that race will be refunded. <br /><br />\r\n9.3.15	If a Member has bet on a runner and that runner is subsequently scratched, the Member''s Bettabucks bet for that race will be refunded. <br /><br />\r\n9.3.16	Tournaments can be cancelled by the TopBetta at any time and all Entry Fees will be credited (as Tournament Dollars) to the Members.<br /><br />\r\n9.3.17	Minimum Prizes are displayed on the Tournament Info page after the start of each competition and will be distributed in accordance with the persons finishing order and the Paid Tournaments Payouts spreadsheet. <br /><br />\r\n9.3.18	In the event that there is a dispute concerning the conduct of a Tournament, the decision of the TopBetta is final and binding and no correspondence will be entered into.<br /><br />\r\n9.3.19	Tournament Prize Winner - The Tournament Winner will be the entrant has the most BettaBucks in his or her Tournament account at the end of the Tournament. <br /><br />\r\n9.3.20	In the event of a tie, the prize will be divided equally between the tying Members.<br /><br />\r\nIn the event that no winner can be determined for a Tournament, the Tournament will be cancelled and Participants will forfeit their entry fee.<br /><br />\r\n9.3.21	This is a game of skill, however the odds of winning the Tournament depend, in part, on the number of contestants and eligible entries received. <br /><br />\r\n9.3.22	Tournament Prize Winner Notification and Acceptance:  The Major Tournament Prize Winners will be notified by email following the determination of the winner.  The Tournament Prize Winners may waive their right to receive the prize and an alternate winner will be selected. <br /><br />\r\n9.3.23	The Tournament Prize is non-assignable and non-transferable.  The Tournament Prize Winner is solely responsible for payment of any taxes on the prize(s).<br /><br />\r\n9.3.24	All Major Winners will be required to complete and return an affidavit of eligibility and publicity and release of liability form within five (5) days of their receipt of such form.  Failure to return the eligibility and release of liability form within five (5) days may result in forfeiture of your prize and your prize will be reallocated.<br /><br />\r\n9.3.25	Reallocation of a prize. Should it be necessary to reallocate a prize, the prize will be allocated to the participant with the next highest amount of TopBetta Bucks. In the event of a tie, the prize will be divided equally between the tying members.<br /><br />\r\n9.3.26	Failure to comply with any term or condition of these Terms and Conditions, may, at the sole discretion of the TopBetta, result in the Major Tournament Prize Winner disqualification, the forfeiture of his or her interest in the prize, and the award of the prize to an alternate winner. <br /><br />\r\n9.3.27	Acceptance of the prize constitutes a winner''s permission (except where prohibited) of TopBetta’s use of the winners'' name and likeness for advertising and promotional purposes as TopBetta so determines without notice, limitation or further compensation.</p>\r\n<p style="padding-left: 10px"><strong>9.4	Tournament Prize Pools</strong><br /><br />\r\n9.4.1	The Prize Pool for a tournament is the sum of the Prize Component of the Entry Fee for that tournament, this will be the total payout amount on completion of the tournament.<br /><br />\r\n9.4.2	Where a tournament is advertising a "guaranteed" minimum prize pool, and if the sum of the Prize Component of the Entry Fee is less than the "guaranteed" minimum prize pool, then "guaranteed" minimum prize pool will be the total payout amount on completion of the tournament.<br /><br />\r\n9.4.3	Where a tournament is advertising a "guaranteed" minimum prize pool, and if the sum of the Prize Component of the Entry Fee is greater than the "guaranteed" minimum prize pool, then the sum of the Prize Component of the Entry Fee will be the total payout amount on completion of the tournament.<br /><br />\r\n9.4.4	Where a Tournament is a single Tournament or the last Tournament in a Jackpot Tournament the winners will receive their winnings as cash and this will be credited to their account balance.<br /><br />\r\n9.4.5	Each Tournament will state a "guaranteed" minimum prize pool.</p>\r\n<p style="padding-left: 10px"><strong>9.5	Tournament Prize Winners</strong><br /><br />\r\n9.5.1	Where the Tournament is not a single Tournament or the last Tournament in a Jackpot Tournament and the prize is entry to the next stage of a Jackpot Tournament, all winners will receive their winnings as Tournament Dollars. Where a Member has won entry to the next stage of the Jackpot Tournament, the entry fee will be deducted from the winnings for automatic entry into the next stage. Any other winners will receive their winnings as Tournament Dollars and this will be credited to their Tournament Dollars account.<br /><br />\r\n9.5.2	Only one entry per Tournament per Member is allowed.  Where a Tournament Prize Winner has an existing entry into the next stage Tournament, their winnings will remain in their account as Tournament Dollars.<br /><br />\r\n9.5.3	Each Tournament will state the minimum number of eligible prize winners.<br /><br />\r\n9.5.4	The number of Tournament Prize Winners is dependent on the number of entrants in the respective tournament.<br /><br />\r\n9.5.5	Where a Tournament Prize winner receives a cash prize, the amount the Tournament Prize Winner receives as their cash prize is dependent on the Prize Pool for that Tournament and is a % of the Prize Pool dependent on the ranking of the Tournament Prize Winner. <br /><br />\r\n9.5.6	Where entries into a subsequent Tournament are the Tournament prize, the number of entries to be awarded is dependent on the size of the Prize Pool. The Prize Pool is divided by the entry fee for the subsequent Tournament, with each complete division resulting in an entry being awarded to a Tournament Prize winner. In the event that there is a surplus remaining in the Prize Pool once all entries are awarded, the next eligible winner receives this surplus in the form of Tournament Dollars.</p>\r\n</div>', 1, 0, 0, 0, '2009-08-19 23:35:05', 62, '', '2010-10-26 04:24:38', 62, 62, '2010-11-15 00:45:20', '2009-08-19 23:35:05', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=', 36, 0, 6, 'version 1.8', '', 0, 146, 'robots=\nauthor=');
INSERT INTO `tbdb_content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES
(5, 'Responsible Gambling', 'responsible-gambling', '', '', '<div class="article">\r\n\r\n<p>TopBetta is committed to the responsible provision of gambling services, and so we provide clients with access to online tools so they can exclude themselves or limit their betting activities.</p>\r\n<p>If you feel that gambling is adversely affecting your life then there are organisations within Australia that can help.</p>\r\n\r\n<span class="subhead">Gambling self-assessment</span>\r\n\r\n<p>The following questionnaire may help you to identify tendencies in your behaviour which are often linked to problem gambling.</p>\r\n<ul>\r\n<li>Have you ever ignored your job in order to gamble?</li>\r\n<li>Have you felt unhappy with your personal life due to gambling?</li>\r\n<li>Does your gambling affect your perception of others, or how others may perceive you?</li>\r\n<li>Has your gambling left you with a deep sense of regret?</li>\r\n<li>Have you ever tried to meet your financial obligations by gambling?</li>\r\n<li>Has gambling affected your motivation to succeed?</li>\r\n<li>Do you feel obliged to gamble in order to recoup lost money?</li>\r\n<li>Do you consider a win as motivation to spend more?</li>\r\n<li>Have you ever spent your last available dollar on gambling?</li>\r\n<li>Have you ever borrowed money in order to gamble?</li>\r\n<li>Have you ever sold assets in order to gamble?</li>\r\n<li>Have you felt reluctant to use money set aside for gambling on everyday purchases?</li>\r\n<li>Has gambling negatively impacted on the way that you treat yourself or others?</li>\r\n<li>Do you find yourself gambling beyond the time you had planned to stop?</li>\r\n<li>Have you ever gambled to avoid dealing with personal problems?</li>\r\n<li>Have you ever committed (or considered committing) a criminal offence in order to gamble?</li>\r\n<li>Does your gambling affect your sleep?</li>\r\n<li>Do the frustrations of your personal life encourage you to gamble?</li>\r\n<li>Have you celebrated good fortune by spending excessive time gambling?</li>\r\n<li>Have you considered harming yourself as a result of gambling?</li>\r\n</ul>\r\n\r\n<p>Studies show that people who have answered "Yes" to seven or more of the above questions show tendencies representative of problem gambling. For further assistance, or if you wish to know more, please contact one of the organisations listed below.</p>\r\n\r\n<span class="subhead">Free and confidential support services</span>\r\n\r\n<p>You can reach your State or Territory helpline by contacting the National Gambling Helpline number on 1800 858 858. The service will automatically re-direct your call to your local organisation.</p>\r\n\r\n<table border="0">\r\n<tr><td>National</td><td>National Gambling Helpline</td><td>1800 858 858</td></tr>\r\n<tr><td>Australian Capital Territory&nbsp;&nbsp;</td><td>Lifeline Canberra</td><td>131 114</td></tr>\r\n<tr><td>New South Wales</td><td>G-Line (NSW) counselling service&nbsp;&nbsp;</td><td>1800 633 635</td></tr>\r\n<tr><td>Northern Territory</td><td>Amity House (Gambling Help)</td><td>1800 629 683</td></tr>\r\n<tr><td>Queensland</td><td>Gambling Helpline</td><td>1800 222 050</td></tr>\r\n<tr><td>South Australia</td><td>Gambling Helpline</td><td>1800 858 858</td></tr>\r\n<tr><td>Victoria</td><td>Gamblers Help (Vic)</td><td>1800 156 789</td></tr>\r\n<tr><td>Western Australia</td><td>G-Line</td><td>1800 622 112</td></tr>\r\n</table>\r\n\r\n</div>', 1, 0, 0, 0, '2010-10-19 02:17:22', 62, '', '2010-10-19 02:24:35', 62, 62, '2010-11-15 00:44:35', '2010-10-19 02:17:22', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=', 5, 0, 3, '', '', 0, 14, 'robots=\nauthor='),
(3, 'Identity Form', 'identity-form', '', '', '<div class="article">\r\n\r\n<span class="subhead">Verifying Your Identify</span>\r\n\r\n<p><strong><a href="components/com_tournaments/images/TopBetta_ID_check_form_v2.pdf">Download the Form</a></strong></p>\r\n\r\n<p>The Australian Federal Government has refined legislation under Anti Money Laundering requirements for licensed Sports Bookmakers to perform mandatory identification validation on all customers. As a result you will need to verify your ID within 90-days of making your first transaction with TopBetta, to do this we require you to please follow the instructions below:</p>\r\n\r\n<p><strong>TopBetta require a certified copy of ONE of these types of photo ID:</strong></p>\r\n<ul>\r\n<li>Current drivers license showing current address, photo and date of birth;</li>\r\n<li>Passport and a certified true copy of a local government or utilities provider within the preceding three months including your current name and details on submission.</li>\r\n</ul>\r\n<p><strong>AND a certified copy of:</strong></p>\r\n<ul>\r\n<li>Recent secondary identification, showing your current address (e.g. utility bill, bank statement, land title, etc)</li>\r\n</ul>\r\n\r\n<p>For clarification or queries on certified documents that comply with the legislation refer to details listed below email TopBetta help@topbetta.com</p>\r\n\r\n<span class="subhead">Certified signed copies of the Identity Form and the required documents can be faxed to (02) 9360 2775 OR posted to:</span>\r\n\r\n<p>TopBetta<br />\r\nLocked Bag 4005<br />\r\n8/78 William Street<br />\r\nSydney NSW 2000</p>\r\n\r\n<p><strong>Who can certify the documents to ensure they meet the legislation requirements?</strong></p>\r\n<ol>\r\n<li>a Justice of the Peace(ie. CPA, Pharmacist, Bank Employee, Lawyer, Defence force officer, Local minister or priest)</li>\r\n<li>a person who is enrolled on the roll of the Supreme Court of a State or Territory, or the High Court of Australia, as a legal practitioner (however described);</li>\r\n<li>a judge of a court;</li>\r\n<li>a magistrate;</li>\r\n<li>a chief executive officer of a Commonwealth court;</li>\r\n<li>a registrar or deputy registrar of a court;</li>\r\n<li>a notary public (for the purposes of the Statutory Declaration Regulations 1993);</li>\r\n<li>a police officer;</li>\r\n<li>an agent of the Australian Postal Corporation who is in charge of an office supplying postal services to the public;</li>\r\n<li>a permanent employee of the Australian Postal Corporation with 2 or more years of continuous service who is employed in an office supplying postal services to the public;</li>\r\n<li>an Australian consular officer or an Australian diplomatic officer (within the meaning of the Consular Fees Act 1955);\r\nan officer with 2 or more continuous years of service with one or more financial institutions (for the purposes of the Statutory Declaration Regulations 1993);</li>\r\n<li>a finance company officer with 2 or more continuous years of service with one or more finance companies (for the purposes of the Statutory Declaration Regulations 1993);</li>\r\n<li>an officer with, or authorised representative of, a holder of an Australian financial services licence, having 2 or more continuous years of service with one or more licensees.</li>\r\n<li>a member of the Institute of Chartered Accountants in Australia, CPA Australia or the National Institute of Accountants with 2 or more years of continuous membership.</li>\r\n</ol>\r\n\r\n<p><strong>What do I have to do to ensure the documents are certified correctly?</strong></p>\r\n\r\n<p>Legible copies should be made of the original documents with no obstruction of the information. As a rule the copies of licenses and passports are supplied enlarged by 200%.</p>\r\n\r\n<p>Clients should print their name their user name on the copies of the original documents and sign the documents in front of the Certifier.</p>\r\n\r\n<p>The Certifier should then state:</p>\r\n\r\n<p>"I certify that this copy is a true copy of the original document that I have sighted.</p>\r\n\r\n<p>The Certifier should then sign and date the documents and include:</p>\r\n<ul>\r\n<li>Full Name</li>\r\n<li>Address</li>\r\n<li>Work and Mobile number</li>\r\n<li>Capacity of Certification (Position in relation to the approved certifier list - see link and or general list outlined above)</li>\r\n</ul>\r\n\r\n<p>The documents will need to be then be posted or faxed to the address supplied above.</p>\r\n</div>', 1, 0, 0, 0, '2010-10-18 06:42:53', 62, '', '2010-10-19 04:35:19', 62, 62, '2010-11-15 00:45:15', '2010-10-18 06:42:53', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=', 11, 0, 5, '', '', 0, 32, 'robots=\nauthor='),
(4, 'Help', 'help', '', '', '\r\n<div class="article">\r\n<p>Need some help? Here''s a list of useful information:</p>\r\n<p><a href="help/how-it-works">How it Works</a> - how tournaments work</p>\r\n<p><a href="help/6">Betting Guide</a> - how to place bets</p>\r\n<p><a href="help/3">Identity Form</a> - required for all users</p>\r\n<p> </p>\r\n<p><a href="help/5">Responsible Gambling</a> - resources related to problem gambling</p>\r\n<p><a href="content/article/2">Terms and Conditions</a></p>\r\n<p><a href="content/article/2#privacy">Privacy Policy</a></p>\r\n</div>', 1, 0, 0, 0, '2010-10-18 23:52:02', 62, '', '2010-11-26 04:50:56', 62, 0, '0000-00-00 00:00:00', '2010-10-18 23:52:02', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=', 16, 0, 4, '', '', 0, 126, 'robots=\nauthor='),
(6, 'Betting Guide', 'betting-guide', '', '', '\r\n<div class="article"><span class="title-img"><img src="components/com_tournament/images/betting-guide.gif" border="0" alt="Betting Guide" /></span>\r\n<p>Here is a brief guide to assist you with betting, if you have any questions please contact us help@topbetta.com.au</p>\r\n<p>You have three options when it comes to making a bet:</p>\r\n<p><strong>WIN</strong> - This is the most popular bet. You just pick the runner you think will finish 1st!</p>\r\n<p><strong>PLACE</strong> - This is where you bet on a specific runner to finish 1st, 2nd, or 3rd. (Note: 3rd place is only paid in races with more than 8 runners.)</p>\r\n<p><strong>EACH WAY</strong> - Not sure which way to go? An Each Way bet combines both Win and Place bets. If your runner wins, you''ll collect on both Win AND Place!</p>\r\n<p>We will be adding more bet types soon and we''ll let you know when!</p>\r\n<br /> <span class="subhead">Placing a Bet</span>\r\n<p>1. <strong>Choose a runner</strong> - There is  lots of information on the TopBetta site to help you select a runner: previous form, trainer, Betta Ratings, Betta Win odds, and Betta Place odds.<br />Once you''ve picked your runner, tick the Selection checkbox in that row. (You can also select multiple runners if you want to place the same bet on several runners.)</p>\r\n<span class="article-img"><img src="components/com_tournament/images/betbox.png" border="0" alt="Choose a runner" /></span> <br />\r\n<p>2. <strong>Bet Type</strong> - Next select your type of bet by clicking one of the "WIN," "PLACE," and "EACH WAY" buttons above the racing form.</p>\r\n<span class="article-img"><img src="components/com_tournament/images/betbuttons.png" border="0" alt="Bet type" /></span> <br />\r\n<p>3. <strong>How Much</strong> - Enter the amount of your bet in the green box labelled "Enter your tournament bet" and click the "Confirm Bets" button.</p>\r\n<span class="article-img"><img src="components/com_tournament/images/betamount.png" border="0" alt="How much" /></span> <br />\r\n<p>4. <strong>Confirmation</strong> - A Bet Confirmation window will appear. Does everything look right? If not, click "Cancel" and amend your bet. If it''s correct, click "Confirm Bet" and your bet will be placed!</p>\r\n<span class="article-img"><img src="components/com_tournament/images/betconfirmation.png" border="0" alt="Confirmation" /></span> <br />\r\n<p>5. <strong>Ticket</strong> - Once your bet has been placed, a Bet Ticket will be shown with the current odds and your approximate win. Now it''s time to cheer on your runner and hopefully collect your winnings!</p>\r\n<span class="article-img"><img src="components/com_tournament/images/betticket.png" border="0" alt="Ticket" /></span> <br />\r\n<p>It''s as easy as that!</p>\r\n<br /> <span class="subhead">Now a quick word on Odds</span>\r\n<p>The odds shown on the Topbetta site come from the NSW TAB. This is a form of parimutuel betting, in which the prize pool is split between everyone who backed the winning runner. This means that if a lot of people choose the same runner, the payout will be less than if it were a longshot. It''s important to note that the final payout odds are not determined until betting is closed. All Betta Win and Betta Place odds are as accurate and up-to-date as possible. Once a race has finished, Topbetta will pay out dividends according to the final "All Paying" status.</p>\r\n</div>', 1, 0, 0, 0, '2010-10-19 04:15:44', 62, '', '2010-11-26 05:18:37', 62, 62, '2010-11-28 23:17:47', '2010-10-19 04:15:44', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=', 4, 0, 2, '', '', 0, 28, 'robots=\nauthor='),
(7, 'Spring Racing 2010 - Final Results', 'winners-201011', '', '<div style="text-align: center; margin: auto; width: 930px;"><img src="components/com_tournament/images/winners/winners_201011.png" border="0" alt="Australia''s Top Punter Spring Racing 2010 - Final Results" /></div>', '', 1, 0, 0, 0, '2010-11-15 00:58:10', 62, '', '2010-11-15 02:19:40', 62, 0, '0000-00-00 00:00:00', '2010-11-15 00:58:10', '0000-00-00 00:00:00', '', '', 'show_title=0\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=', 4, 0, 1, '', '', 0, 6, 'robots=\nauthor=');


